import React, { useState, useEffect } from "react"
import Carousel from 'react-elastic-carousel'

import Card from '../CarouselCard/CarouselCard'
import './Carousel.scss'

import carousel1 from '../../assets/carousel_1.png'
import carousel2 from '../../assets/carousel_2.png'
import carousel3 from '../../assets/carousel_3.png'

const images = [
    {src: carousel1},
    {src: carousel2},
    {src: carousel3},
]

const breakPoints = [
    {width: 300, itemsToShow: 1},
    {width: 500, itemsToShow: 2},
    {width: 768, itemsToShow: 3},
    {width: 1200, itemsToShow: 1}
]
function CarouselComponent() {

    return (
        <Carousel breakPoints={breakPoints}>
            {images.map(key => <Card key={key} src={key.src} />)}
        </Carousel>
    )
}
export default CarouselComponent
