import React, { useState, useEffect } from "react"
import './Carousel.scss'

import Card from '../CarouselCard/CarouselCard'

import carousel1 from '../../assets/carousel_1.png'
import carousel2 from '../../assets/carousel_2.png'
import carousel3 from '../../assets/carousel_3.png'

function CarouselComponent() {
    const [currentI, setCurrentI] = useState(0)

    const cards = [
        {src: carousel1},
        {src: carousel2},
        {src: carousel3}
    ]

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentI( (current) => 
                current === cards.length - 1 ? 0 : current + 1
            )
        }, 2000)
    })

    return (
        <div className="carousel">
            {cards.map((key, i) => {
            return <Card 
                key={i} 
                src={key.src} 
                className={i === cards.length - 1 ? 'slider-image-next' : i === 0 ? 'slider-image-prev' : ''}/>
            })}
        </div>
    )
}
export default CarouselComponent
