import React, { useState, useEffect } from "react"
import './Carousel.scss'

import carousel1 from '../../assets/carousel_1.png'
import carousel2 from '../../assets/carousel_2.png'
import carousel3 from '../../assets/carousel_3.png'

const images = [
    <img key={carousel1} src={carousel1} alt="carousel_image"/>,
    <img key={carousel2} src={carousel2} alt="carousel_image"/>,
    <img key={carousel3} src={carousel3} alt="carousel_image"/>,
    <img key={carousel1} src={carousel1} alt="carousel_image"/>,
    <img key={carousel2} src={carousel2} alt="carousel_image"/>,
    <img key={carousel3} src={carousel3} alt="carousel_image"/>
]



function CarouselComponent() {
    const [activeIndex, setActiveIndex] = useState(0)

    useEffect(() => {
        const interval = setInterval(() => {
            setActiveIndex((current) => {
                return current === images.length - 1 ? 0 : current + 1
            })
        }, 4000)
        return () => clearInterval()
    }, [])

    const prevImageIndex = activeIndex ? activeIndex - 1 : images.length - 1
    const nextImageIndex = activeIndex === images.length - 1 ? 0 : activeIndex + 1

    return (
        <div className="carousel">
            <div 
                className="carousel-image carousel-image-prev"
                key={prevImageIndex}
            >
                {images[prevImageIndex]}
            </div>

            <div 
                className="carousel-image"
                key={activeIndex + 1}
            >
                {images[activeIndex + 1]}
            </div>



            <div 
                className="carousel-image carousel-image-next"
                key={nextImageIndex}
            >
                {images[nextImageIndex]}
            </div>
        </div>
    )
}
export default CarouselComponent
