import React, { useState, useEffect } from "react"
import './Carousel.scss'

import carousel1 from '../../assets/carousel_1.png'
import carousel2 from '../../assets/carousel_2.png'
import carousel3 from '../../assets/carousel_3.png'

const images = [
    <img key={carousel1} src={carousel1} alt="carousel_image"/>,
    <img key={carousel3} src={carousel2} alt="carousel_image"/>,
    <img key={carousel3} src={carousel3} alt="carousel_image"/>
]



function CarouselComponent() {
    const [activeIndex, setActiveIndex] = useState(0)

    useEffect(() => {
        const interval = setInterval(() => {
            setActiveIndex((current) => {
                return current === images.length - 1 ? 0 : current + 1
            })
        }, 5000); 
    }, [])

    return (
        <div className="carousel">
            <div className="carousel-image"></div>
            <div className="carousel-image"></div>
            <div className="carousel-image"></div>
        </div>
    )
}
export default CarouselComponent
