import { Carousel } from 'react-responsive-carousel'
import './SeriesSlider.scss'

import image from '../../assets/series_offroad_image.png'
import { useEffect, useState } from 'react'

const productsImages = [
    <img key={image} src={image} alt="src" />,
    <img key={image} src={image} alt="src" />,
    <img key={image} src={image} alt="src" />
]

function SeriesSlider() {
    const [activeIndex, setActiveIndex] = useState(0) 

    useEffect(() => {
        const interval = setInterval(() => {
            setActiveIndex((current) => {
                return current === productsImages.length - 1 ? 0 : current + 1
            })
        }, 5000)

        return () => clearInterval(interval)
    }, [])

    const prevImageIndex = activeIndex ? activeIndex - 1 : productsImages.length - 1
    const nextImageIndex = activeIndex === productsImages.length - 1 ? 0 : activeIndex + 1

    return (
        <div className="slider">
            <div 
                className="slider-image slider-image-prev"
                key={prevImageIndex}
            >
                {images[prevImageIndex]}
            </div>

            <div
                className="slider-image"
                key={activeIndex}
            >
                {images[activeIndex]}
            </div>

            <div
                className="slider-image slider-image-next"
                key={nextImageIndex}
            >
                {images[nextImageIndex]}
            </div>
        </div>
    )
}

export default SeriesSlider
