import React, { useState, useEffect } from "react"
import './Carousel.scss'

import Card from '../CarouselCard/CarouselCard'

import carousel1 from '../../assets/carousel_1.png'
import carousel2 from '../../assets/carousel_2.png'
import carousel3 from '../../assets/carousel_3.png'

function CarouselComponent() {
    const [currentI, setCurrentI] = useState(0)

    const cards = [
        carousel1,
        carousel2,
        carousel3
    ]

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentI( (current) => 
                current === cards.length - 1 ? 0 : current + 1
            )
        }, 4000)

        return () => clearInterval()
    }, [])

    const prevImageIndex = currentI ? currentI - 1 : cards.length - 1
    const nextImageIndex = currentI === cards.length - 1 ? 0 : currentI + 1

    return (
        <div className="carousel">
            {cards.map((key, i) => {
            return <Card 
                key={i === cards.length - 1 ? nextImageIndex : i === 0 ? prevImageIndex : currentI} 
                src={i === cards.length - 1 ? key[nextImageIndex] : i === 0 ? key[prevImageIndex] : key[currentI]} 
                className={i === cards.length - 1 ? 'slider-image-next' : i === 0 ? 'slider-image-prev' : ''}/>
            })}
        </div>
    )
}
export default CarouselComponent
