import React from "react"
import Carousel from "react-multi-carousel"

import './Carousel.scss'

import carousel1 from '../../assets/carousel_1.png'
import carousel2 from '../../assets/carousel_2.png'
import carousel3 from '../../assets/carousel_3.png'

const images = [
    <img key={carousel3} src={carousel3} alt="carousel_image" />,
    <img key={carousel1} src={carousel1} alt="carousel_image" />,
    <img key={carousel2} src={carousel2} alt="carousel_image" />,
    <img key={carousel3} src={carousel3} alt="carousel_image" />,
]

const responsive = {
    desktop: {
        breakpoint: { max: 3000, min: 1024 },
        items: 2,
        slidesToSlide: 1
    },
    tablet: {
        breakpoint: { max: 1124, min: 464 },
        items: 1,
        slidesToSlide: 1
    },
    mobile: {
        breakpoint: { max: 464, min: 0 },
        items: 1,
        slidesToSlide: 1
    }
}

function CarouselComponent() {

    return (
        <Carousel
            className="carousel"
            infinite={false}
            centerMode={true}
            showDots={false}
            responsive={responsive}
            swipeable={true}
            draggable={false}
            focusOnSelect = {true}
            customTransition = "all ease-in-out .6s" 
            transitionDuration={0}
        >
            {images.map(key => <div className="slider-item" key={key}>{key}</div>)}
        </Carousel>
    )
}
export default CarouselComponent
